import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const EditCar = () => {
  const { id } = useParams();
  const [brand, setBrand] = useState('');
  const [model, setModel] = useState('');
  const [power, setPower] = useState('');
  const [maxSpeed, setMaxSpeed] = useState('');
  const [image, setImage] = useState(null);

  useEffect(() => {
    // Завантажте дані машини за ID (наприклад, із контексту або API)
    // Замість цього зробимо запит, якщо дані існують
    console.log('Завантаження даних машини для редагування:', id);
  }, [id]);

  const handleSubmit = (e) => {
    e.preventDefault();
    const updatedCar = { brand, model, power, maxSpeed, image };
    // Додайте функцію для оновлення машини
    console.log('Оновлена машина:', updatedCar);
  };

  return (
    <div>
      <h2>Редагувати машину</h2>
      <form onSubmit={handleSubmit}>
        {/* Такі самі поля, як у CreateCar */}
        <button type="submit">Оновити</button>
      </form>
    </div>
  );
};

export default EditCar;
