import React, { useState } from 'react';
import { cars } from './car_stock';
import CarCard from './carcrad';

const Catalog = () => {
  // Відбираємо автомобілі для каталогу, починаючи з 11-го
  const catalogCars = cars.slice(10);
  const [carList, setCarList] = useState(catalogCars);
  const [totalSpeed, setTotalSpeed] = useState(0);

  const handleSearch = (event) => {
    const searchTerm = event.target.value.toLowerCase();
    const filteredCars = catalogCars.filter(car => car.brand.toLowerCase().includes(searchTerm));
    setCarList(filteredCars);
  };

  const handleSortAsc = () => {
    const sortedCars = [...carList].sort((a, b) => a.power - b.power);
    setCarList(sortedCars);
  };

  const handleSortDesc = () => {
    const sortedCars = [...carList].sort((a, b) => b.power - a.power);
    setCarList(sortedCars);
  };

  const handleTotalSpeed = () => {
    const total = carList.reduce((sum, car) => sum + car.maxSpeed, 0);
    setTotalSpeed(total);
  };

  return (
    <div className="catalog-page">
      <h1>Каталог Автомобілів</h1>
      <div className="controls" style={{ textAlign: 'center', marginBottom: '20px' }}>
        <button onClick={handleSortAsc}>Sort by Power (down)</button>
        <button onClick={handleSortDesc}>Sort by Power (up)</button>
        <input type="text" placeholder="Search by brand" onChange={handleSearch} />
        <button onClick={handleTotalSpeed}>Count Total Speed</button>
      </div>
      <div id="total-speed-container" style={{ textAlign: 'center', marginBottom: '20px' }}>
        <h3>Total Max Speed of Cars in Catalog: <span>{totalSpeed}</span> km/h</h3>
      </div>
      <div className="catalog-grid">
        {carList.map((car, index) => (
          <CarCard key={index} car={car} />
        ))}
      </div>
    </div>
  );
};

export default Catalog;
