import React, { useState, useContext } from 'react';
import { CarContext } from './carcontext';

const EditCar = () => {
  const { cars, updateCar } = useContext(CarContext);
  const [searchTerm, setSearchTerm] = useState('');
  const [car, setCar] = useState(null);

  const handleSearch = () => {
    const foundCar = cars.find(c => c.brand.toLowerCase().includes(searchTerm.toLowerCase()));
    if (foundCar) {
      setCar(foundCar);
    } else {
      alert("Car not found.");
    }
  };

  const handleChange = (e) => {
    setCar({ ...car, [e.target.name]: e.target.value });
  };

  const handleUpdate = () => {
    updateCar(car);
    alert("Car updated successfully!");
  };

  return (
    <div className="form-container">
      <h2 className="form-title">Edit Your Car</h2>
      <div className="form-group">
        <label>Enter car name to search:</label>
        <input 
          type="text" 
          placeholder="Car name" 
          value={searchTerm} 
          onChange={(e) => setSearchTerm(e.target.value)} 
        />
        <button onClick={handleSearch} style={{ marginTop: '1rem' }}>Search</button>
      </div>

      {car && (
        <>
          <div className="form-group">
            <label>Brand:</label>
            <input 
              type="text" 
              name="brand" 
              value={car.brand} 
              onChange={handleChange} 
            />
          </div>
          <div className="form-group">
            <label>Model:</label>
            <input 
              type="text" 
              name="model" 
              value={car.model} 
              onChange={handleChange} 
            />
          </div>
          <div className="form-group">
            <label>Power:</label>
            <input 
              type="number" 
              name="power" 
              value={car.power} 
              onChange={handleChange} 
            />
          </div>
          <div className="form-group">
            <label>Max Speed:</label>
            <input 
              type="number" 
              name="maxSpeed" 
              value={car.maxSpeed} 
              onChange={handleChange} 
            />
          </div>
          <div className="form-group">
            <label>Photo:</label>
            <input 
              type="file" 
              name="photo" 
              onChange={(e) => setCar({ ...car, photo: e.target.files[0] })} 
            />
          </div>
          <div className="form-actions">
            <button onClick={handleUpdate}>Update</button>
          </div>
        </>
      )}
    </div>
  );
};

export default EditCar;
