import React, { useContext, useState } from 'react';
import { ItemContext } from './itemcontext';
import { Link } from 'react-router-dom';

const Catalog = () => {
  const { items } = useContext(ItemContext);
  const [filteredItems, setFilteredItems] = useState(items);
  const [searchTerm, setSearchTerm] = useState('');

  const handleFilter = (property, value) => {
    const newItems = items.filter((item) => item[property] === value);
    setFilteredItems(newItems);
  };

  const handleSearch = (event) => {
    const term = event.target.value.toLowerCase();
    setSearchTerm(term);
    const newItems = items.filter((item) => 
      item.brand.toLowerCase().includes(term) || item.model.toLowerCase().includes(term)
    );
    setFilteredItems(newItems);
  };

  return (
    <div>
      <input type="text" placeholder="Search by brand or model" onChange={handleSearch} value={searchTerm} />
      <button onClick={() => handleFilter('brand', 'Toyota')}>Filter by Toyota</button>
      <div className="catalog-grid">
        {filteredItems.map((item) => (
          <div key={item.id} className="car-card">
            <h3>{item.brand} {item.model}</h3>
            <Link to={`/item/${item.id}`}>View more</Link> {/* Посилання на сторінку Item */}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Catalog;
