import React, { useState } from 'react';
import { cars } from './car_stock'; // Імпорт даних

const Main = () => {
  const [carList, setCarList] = useState(cars);
  const [totalSpeed, setTotalSpeed] = useState(0);

  const handleSearch = (event) => {
    const searchTerm = event.target.value.toLowerCase();
    const filteredCars = cars.filter(car => car.brand.toLowerCase().includes(searchTerm));
    setCarList(filteredCars);
  };

  const handleSortAsc = () => {
    const sortedCars = [...carList].sort((a, b) => a.power - b.power);
    setCarList(sortedCars);
  };

  const handleSortDesc = () => {
    const sortedCars = [...carList].sort((a, b) => b.power - a.power);
    setCarList(sortedCars);
  };

  const handleTotalSpeed = () => {
    const total = carList.reduce((sum, car) => sum + car.maxSpeed, 0);
    setTotalSpeed(total);
  };

  return (
    <main>
      <h1 className="section-title">Our Car Collection</h1>
      <div id="total-speed-container">
        <h3>Total Max Speed of All Cars: <span>{totalSpeed}</span> km/h</h3>
      </div>
      <div className="controls">
        <button onClick={handleSortAsc}>Sort by Power (down)</button>
        <button onClick={handleSortDesc}>Sort by Power (up)</button>
        <input type="text" placeholder="Search by brand" onChange={handleSearch} />
        <button onClick={handleTotalSpeed}>Count Total Speed</button>
      </div>
      <div id="car-grid">
        {carList.map((car, index) => (
          <div key={index} className="car-card">
            <img src={car.image} alt={`${car.brand} ${car.model}`} />
            <div className="car-info">
              <p><strong>Brand:</strong> {car.brand}</p>
              <p><strong>Model:</strong> {car.model}</p>
              <p><strong>Power:</strong> {car.power} hp</p>
              <p><strong>Max Speed:</strong> {car.maxSpeed} km/h</p>
            </div>
          </div>
        ))}
      </div>
    </main>
  );
};

export default Main;
