import React, { useState, useContext } from 'react';
import { CarContext } from './carcontext';
import './style.css';

const EditCar = () => {
  const { cars, updateCar } = useContext(CarContext);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCar, setSelectedCar] = useState(null);

  const handleSearch = () => {
    const foundCars = cars.filter(car => car.brand.toLowerCase().startsWith(searchTerm.toLowerCase()));
    if (foundCars.length > 0) {
      setSelectedCar(foundCars[0]); // Вибираємо першу знайдену машину
    } else {
      setSelectedCar(null);
    }
  };

  const handleUpdate = () => {
    if (selectedCar) {
      updateCar(selectedCar);
      alert('Car updated successfully!');
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setSelectedCar(prev => ({
      ...prev,
      [name]: value,
    }));
  };

  return (
    <div className="edit-car-container">
      <h2>Edit Car</h2>
      <div className="search-section">
        <label>Enter car name to search:</label>
        <input
          type="text"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          placeholder="Car name"
          className="search-input"
        />
        <button onClick={handleSearch} className="search-button">Search</button>
      </div>

      {selectedCar && (
        <div className="edit-section">
          <label>Brand:</label>
          <input
            type="text"
            name="brand"
            value={selectedCar.brand}
            onChange={handleInputChange}
            className="input-field"
          />
          <label>Model:</label>
          <input
            type="text"
            name="model"
            value={selectedCar.model}
            onChange={handleInputChange}
            className="input-field"
          />
          <label>Power:</label>
          <input
            type="number"
            name="power"
            value={selectedCar.power}
            onChange={handleInputChange}
            className="input-field"
          />
          <label>Max Speed:</label>
          <input
            type="number"
            name="maxSpeed"
            value={selectedCar.maxSpeed}
            onChange={handleInputChange}
            className="input-field"
          />
          <label>Photo:</label>
          <input
            type="file"
            name="photo"
            className="input-field"
          />
          <button onClick={handleUpdate} className="update-button">Update</button>
        </div>
      )}
    </div>
  );
};

export default EditCar;
