import React, { useState, useContext } from 'react';
import { CarContext } from './carcontext';

const EditCar = () => {
  const { cars, updateCar } = useContext(CarContext);
  const [searchName, setSearchName] = useState('');
  const [car, setCar] = useState(null); // Обрана машина для редагування

  const handleSearch = () => {
    // Знайти машину за назвою
    const foundCar = cars.find((c) => c.brand.toLowerCase() === searchName.toLowerCase());
    if (foundCar) {
      setCar({ ...foundCar });
    } else {
      alert('Машину не знайдено');
      setCar(null);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCar((prevCar) => ({ ...prevCar, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (car) {
      updateCar(car); // Оновити машину в контексті
      alert('Машину успішно оновлено!');
    } else {
      alert('Будь ласка, знайдіть машину для редагування');
    }
  };

  return (
    <div className="edit-car-page">
      <h2>Редагувати машину</h2>
      <div className="search-section">
        <label>
          Введіть назву машини для пошуку:
          <input
            type="text"
            value={searchName}
            onChange={(e) => setSearchName(e.target.value)}
            placeholder="Назва машини"
          />
        </label>
        <button onClick={handleSearch}>Знайти</button>
      </div>

      {car && (
        <form onSubmit={handleSubmit}>
          <label>
            Бренд:
            <input type="text" name="brand" value={car.brand} onChange={handleChange} />
          </label>
          <label>
            Модель:
            <input type="text" name="model" value={car.model} onChange={handleChange} />
          </label>
          <label>
            Потужність:
            <input type="number" name="power" value={car.power} onChange={handleChange} />
          </label>
          <label>
            Макс. швидкість:
            <input type="number" name="maxSpeed" value={car.maxSpeed} onChange={handleChange} />
          </label>
          <label>
            Фото:
            <input type="text" name="image" value={car.image} onChange={handleChange} />
          </label>
          <button type="submit">Зберегти зміни</button>
        </form>
      )}
    </div>
  );
};

export default EditCar;
