import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const EditCar = () => {
  const [carId, setCarId] = useState('');
  const navigate = useNavigate();

  const handleEdit = () => {
    if (carId) {
      // Перенаправляємо користувача на сторінку редагування конкретної машини
      navigate(`/edit/${carId}`);
    } else {
      alert("Введіть ідентифікатор машини для редагування.");
    }
  };

  return (
    <div className="edit-car-page">
      <h2>Редагувати машину</h2>
      <label>
        Введіть ідентифікатор машини для редагування:
        <input 
          type="text" 
          value={carId} 
          onChange={(e) => setCarId(e.target.value)} 
          placeholder="ID машини" 
        />
      </label>
      <button onClick={handleEdit}>Редагувати</button>
    </div>
  );
};

export default EditCar;
