import axios from 'axios';

const API_URL = 'http://localhost:5008'; // Порт вашого сервера

// Функція для отримання автомобілів з пошуковим терміном
export const fetchCars = async (searchTerm) => {
  try {
    const response = await axios.get(`${API_URL}/cars`, {
      params: { search: searchTerm }
    });
    return response.data;  // Повертаємо відфільтровані дані
  } catch (error) {
    console.error('Помилка при отриманні даних:', error);
    return [];
  }
};
