import React, { useState, useEffect } from 'react';
import { fetchCars } from './api';  // Імпортуємо функції API
import './catalog.css';

const Catalog = () => {
  const [cars, setCars] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [totalSpeed, setTotalSpeed] = useState(0);  
  const [sortOrder, setSortOrder] = useState(''); // Сортування
  const [filteredCars, setFilteredCars] = useState([]);

  useEffect(() => {
    const loadCars = async () => {
      const data = await fetchCars(searchTerm, sortOrder);  // Отримуємо автомобілі на основі пошуку
      setCars(data);
      setFilteredCars(data);
    };

    loadCars();
  }, [searchTerm, sortOrder]);

  // Функція для сортування автомобілів
  const sortCars = (order) => {
    setSortOrder(order);  // Оновлюємо стан для сортування
  };

  const calculateTotalSpeed = () => {
    const total = filteredCars.reduce((acc, car) => acc + car.maxSpeed, 0);
    setTotalSpeed(total);
  };

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);  // Оновлюємо значення пошукового терміну
  };

  const handleCarClick = (carId) => {
    // Перехід на сторінку деталів
    window.location.href = `/item/${carId}`;
  };

  return (
    <div className="container">
      <h2>Каталог автомобілів</h2>

      {/* Панель пошуку */}
      <input
        type="text"
        placeholder="Пошук..."
        value={searchTerm}
        onChange={handleSearchChange}
      />

      {/* Кнопки сортування */}
      <div className="controls">
        <button onClick={() => sortCars('asc')}>Сортувати за зростанням</button>
        <button onClick={() => sortCars('desc')}>Сортувати за спаданням</button>
      </div>

      <div id="total-speed-container">
        <h3>Загальна швидкість всіх автомобілів: {totalSpeed} км/год</h3>
      </div>

      <button className="count-total-speed" onClick={calculateTotalSpeed}>
        Підрахувати загальну швидкість
      </button>

      <div className="catalog-grid">
        {filteredCars.length > 0 ? (
          filteredCars.map((car) => (
            <div key={car.id} className="car-card" onClick={() => handleCarClick(car.id)}>
              <h3>{car.brand} {car.model}</h3>
              <p>Макс. швидкість: {car.maxSpeed} км/год</p>
              <img src={`http://localhost:5008${car.image}`} alt={car.model} />
            </div>
          ))
        ) : (
          <p>Нічого не знайдено</p>
        )}
      </div>
    </div>
  );
};

export default Catalog;
