import React, { useState, useEffect } from 'react';
import { fetchCars } from './api';  // Імпортуємо функції API
import './catalog.css';

const Catalog = () => {
  const [cars, setCars] = useState([]);
  const [sortedCars, setSortedCars] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCar, setSelectedCar] = useState(null);  // Вибраний автомобіль для деталей
  const [selectedConfig, setSelectedConfig] = useState('Базова');
  const [selectedCount, setSelectedCount] = useState(1);  // Кількість автомобілів

  useEffect(() => {
    const loadCars = async () => {
      const data = await fetchCars();  // Отримуємо всі автомобілі
      setCars(data);
      setSortedCars(data);  // Ініціалізуємо відсортовані дані
    };

    loadCars();
  }, []);

  // Функція для сортування автомобілів
  const sortCars = (order) => {
    const sorted = [...cars];
    sorted.sort((a, b) => {
      if (order === 'asc') {
        return a.maxSpeed - b.maxSpeed;  // Сортування за зростанням
      } else {
        return b.maxSpeed - a.maxSpeed;  // Сортування за спаданням
      }
    });
    setSortedCars(sorted);
  };

  // Підрахунок загальної швидкості всіх автомобілів
  const calculateTotalSpeed = () => {
    const totalSpeed = cars.reduce((acc, car) => acc + car.maxSpeed, 0);
    return totalSpeed;  // Повертаємо загальну швидкість
  };

  // Фільтрація автомобілів по пошуковому терміну
  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  // Фільтрація та пошук по моделях або брендам
  const filterCars = () => {
    return sortedCars.filter((car) =>
      car.brand.toLowerCase().includes(searchTerm.toLowerCase()) ||
      car.model.toLowerCase().includes(searchTerm.toLowerCase())
    );
  };

  // Вибір автомобіля для відображення деталей
  const handleCarClick = (car) => {
    setSelectedCar(car);  // Вибір автомобіля
  };

  // Функція для скидання вибору автомобіля
  const handleDeselectCar = () => {
    setSelectedCar(null);  // Скидання вибору
  };

  return (
    <div className="container">
      <h2>Каталог автомобілів</h2>

      {/* Панель пошуку */}
      <input
        type="text"
        placeholder="Пошук..."
        value={searchTerm}
        onChange={handleSearchChange}
      />

      {/* Кнопки сортування */}
      <div className="controls">
        <button onClick={() => sortCars('asc')}>Сортувати за зростанням</button>
        <button onClick={() => sortCars('desc')}>Сортувати за спаданням</button>
      </div>

      {/* Підрахунок загальної швидкості */}
      <div id="total-speed-container">
        <h3>Загальна швидкість всіх автомобілів: {calculateTotalSpeed()} км/год</h3>
      </div>

      {/* Вибраний автомобіль або каталог */}
      {selectedCar ? (
        <div className="car-details">
          <h3>{selectedCar.brand} {selectedCar.model}</h3>
          <p>Потужність: {selectedCar.power} hp</p>
          <p>Макс. швидкість: {selectedCar.maxSpeed} км/год</p>
          <img src={`http://localhost:5004${selectedCar.image}`} alt={selectedCar.model} />

          {/* Вибір комплектації та кількості */}
          <div className="select-field">
            <label>Комплектація:</label>
            <select value={selectedConfig} onChange={(e) => setSelectedConfig(e.target.value)}>
              <option value="Базова">Базова</option>
              <option value="Комфорт">Комфорт</option>
              <option value="Спорт">Спорт</option>
              <option value="Люкс">Люкс</option>
            </select>
          </div>

          <div className="select-field">
            <label>Кількість:</label>
            <select value={selectedCount} onChange={(e) => setSelectedCount(Number(e.target.value))}>
              {Array.from({ length: 20 }, (_, i) => i + 1).map((count) => (
                <option key={count} value={count}>{count}</option>
              ))}
            </select>
          </div>

          <button>Додати до кошика</button>
          <button className="back-button" onClick={handleDeselectCar}>Назад до каталогу</button>
        </div>
      ) : (
        // Відображення списку автомобілів, якщо жоден не вибраний
        <div className="catalog-grid">
          {filterCars().length > 0 ? (
            filterCars().map((car) => (
              <div key={car.id} className="car-card" onClick={() => handleCarClick(car)}>
                <h3>{car.brand} {car.model}</h3>
                <p>Макс. швидкість: {car.maxSpeed} км/год</p>
                <img src={`http://localhost:5004${car.image}`} alt={car.model} />
              </div>
            ))
          ) : (
            <p>Нічого не знайдено</p>
          )}
        </div>
      )}
    </div>
  );
};

export default Catalog;
