import React, { useContext, useState } from 'react';
import { CarContext } from './carcontext';
import CarCard from './carcrad'; // Fixed typo in import
import Spinner from './spinner';
import './catalog.css';
import './button.css';
import './footer.css';

const Catalog = () => {
  const { carList, totalSpeed, handleSearch, handleSortAsc, handleSortDesc, handleTotalSpeed, isLoading } = useContext(CarContext);
  const [searchTerm, setSearchTerm] = useState(''); // Додаємо стан для пошукового терміна
  const [suggestions, setSuggestions] = useState([]); // Стан для збереження пропозицій

  const handleSearchChange = (e) => {
    const value = e.target.value;
    setSearchTerm(value);

    // Фільтруємо список для пропозицій
    if (value) {
      const filteredSuggestions = carList.filter(car =>
        car.brand.toLowerCase().startsWith(value.toLowerCase())
      );
      setSuggestions(filteredSuggestions);
    } else {
      setSuggestions([]);
    }
  };

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    handleSearch(searchTerm); // Викликаємо пошук
    setSuggestions([]); // Очищаємо пропозиції після пошуку
  };

  const handleSuggestionClick = (suggestion) => {
    setSearchTerm(suggestion.brand); // Встановлюємо вибрану пропозицію в поле пошуку
    handleSearch(suggestion.brand); // Викликаємо пошук для вибраної пропозиції
    setSuggestions([]); // Очищаємо пропозиції після вибору
  };

  if (isLoading) {
    return <Spinner />;
  }

  return (
    <div className="catalog-page">
      <div className="controls">
        <button onClick={handleSortAsc}>Sort by Power (down)</button>
        <button onClick={handleSortDesc}>Sort by Power (up)</button>

        <form onSubmit={handleSearchSubmit}>
          <input
            type="text"
            placeholder="Search by brand"
            value={searchTerm}
            onChange={handleSearchChange}
          />
          <button type="submit">Search</button>
        </form>

        {/* Відображаємо пропозиції під полем пошуку */}
        {suggestions.length > 0 && (
          <div className="suggestions">
            {suggestions.map((suggestion, index) => (
              <div
                key={index}
                onClick={() => handleSuggestionClick(suggestion)}
                className="suggestion-item"
              >
                {suggestion.brand}
              </div>
            ))}
          </div>
        )}

        <button onClick={handleTotalSpeed}>Count Total Speed</button>
      </div>

      <div id="total-speed-container">
        <h3>Total Max Speed of Cars in Catalog: <span>{totalSpeed}</span> km/h</h3>
      </div>

      <div className="catalog-grid">
        {carList.map((car, index) => (
          <CarCard key={car.id || index} car={car} /> 
        ))}
      </div>
    </div>
  );
};

export default Catalog;
