import React, { useState, useEffect } from 'react';
import { fetchCars } from './api';  // Імпортуємо функції API
import './catalog.css';

const Catalog = () => {
  const [cars, setCars] = useState([]);
  const [sortedCars, setSortedCars] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCar, setSelectedCar] = useState(null);  // Стейт для збереження вибраного автомобіля
  const [sortOrder, setSortOrder] = useState('asc'); // 'asc' для зростання, 'desc' для спадання

  useEffect(() => {
    const loadCars = async () => {
      const data = await fetchCars();  // Отримуємо всі автомобілі
      setCars(data);
      setSortedCars(data);  // Ініціалізуємо відсортовані дані
    };

    loadCars();
  }, []);

  // Функція для сортування автомобілів
  const sortCars = (order) => {
    const sorted = [...cars];
    sorted.sort((a, b) => {
      if (order === 'asc') {
        return a.maxSpeed - b.maxSpeed;  // Сортування за зростанням
      } else {
        return b.maxSpeed - a.maxSpeed;  // Сортування за спаданням
      }
    });
    setSortedCars(sorted);
  };

  // Підрахунок середньої швидкості всіх автомобілів
  const calculateAverageSpeed = () => {
    const totalSpeed = cars.reduce((acc, car) => acc + car.maxSpeed, 0);
    return (totalSpeed / cars.length).toFixed(2);  // Повертаємо середнє значення з 2 знаками після коми
  };

  // Фільтрація автомобілів по пошуковому терміну
  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  // Фільтрація та пошук по моделях або брендам
  const filterCars = () => {
    return sortedCars.filter((car) =>
      car.brand.toLowerCase().includes(searchTerm.toLowerCase()) ||
      car.model.toLowerCase().includes(searchTerm.toLowerCase())
    );
  };

  // Функція для вибору конкретного автомобіля
  const handleCarClick = (car) => {
    setSelectedCar(car);  // Зберігаємо вибраний автомобіль в стейт
  };

  // Функція для скидання вибору автомобіля
  const handleDeselectCar = () => {
    setSelectedCar(null);  // Очищаємо вибір
  };

  return (
    <div>
      <h2>Каталог автомобілів</h2>

      {/* Панель пошуку */}
      <input
        type="text"
        placeholder="Пошук..."
        value={searchTerm}
        onChange={handleSearchChange}
      />

      {/* Кнопки сортування */}
      <div>
        <button onClick={() => sortCars('asc')}>Сортувати за зростанням</button>
        <button onClick={() => sortCars('desc')}>Сортувати за спаданням</button>
      </div>

      {/* Підрахунок середньої швидкості */}
      <div>
        <h3>Середня швидкість всіх автомобілів: {calculateAverageSpeed()} км/год</h3>
      </div>

      {/* Відображення вибраного автомобіля (якщо він вибраний) */}
      {selectedCar ? (
        <div className="car-details">
          <h3>{selectedCar.brand} {selectedCar.model}</h3>
          <p>Потужність: {selectedCar.power} hp</p>
          <p>Макс. швидкість: {selectedCar.maxSpeed} км/год</p>
          <img src={`http://localhost:5004${selectedCar.image}`} alt={selectedCar.model} />
          <button onClick={handleDeselectCar}>Назад до каталогу</button>
        </div>
      ) : (
        // Відображення списку автомобілів, якщо жоден не вибраний
        <div className="catalog-grid">
          {filterCars().length > 0 ? (
            filterCars().map((car) => (
              <div key={car.id} className="car-card">
                <h3>{car.brand} {car.model}</h3>
                <p>Макс. швидкість: {car.maxSpeed} км/год</p>
                <img
                  src={`http://localhost:5004${car.image}`}
                  alt={car.model}
                  onClick={() => handleCarClick(car)}  // Натискання на картинку вибирає машину
                />
              </div>
            ))
          ) : (
            <p>Нічого не знайдено</p>
          )}
        </div>
      )}
    </div>
  );
};

export default Catalog;
