import React, { useContext, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { CarContext } from './carcontext';
import { useDispatch } from 'react-redux';
import { addToCart } from './cartSlice';
import './item.css';

const Item = () => {
  const { id } = useParams(); // Отримуємо ID з URL
  const navigate = useNavigate(); // Для навігації
  const dispatch = useDispatch(); // Для додавання до кошика
  const { carList } = useContext(CarContext); // Отримуємо список машин з контексту
  const car = carList.find((car) => car.id === parseInt(id)); // Знаходимо потрібну машину

  const [selectedConfig, setSelectedConfig] = useState('Базова'); // Вибір комплектації
  const [selectedCount, setSelectedCount] = useState(1); // Вибір кількості

  if (!car) {
    return <div>Машина не знайдена</div>;
  }

  const configurations = ['Базова', 'Комфорт', 'Спорт', 'Люкс']; // Список комплектацій
  const counts = Array.from({ length: 20 }, (_, i) => i + 1); // Список чисел від 1 до 20

  const handleAddToCart = () => {
    dispatch(addToCart({ ...car, config: selectedConfig, count: selectedCount }));
    alert('Машину додано до кошика!');
  };

  return (
    <div className="car-details-page">
      <img src={car.image} alt={car.model} />
      <h2>{car.brand} {car.model}</h2>
      <p>Потужність: {car.power} hp</p>
      <p>Макс. швидкість: {car.maxSpeed} км/год</p>

      {/* Вибір комплектації */}
      <div className="select-section">
        <label htmlFor="config">Виберіть комплектацію:</label>
        <select
          id="config"
          value={selectedConfig}
          onChange={(e) => setSelectedConfig(e.target.value)}
        >
          {configurations.map((config, index) => (
            <option key={index} value={config}>{config}</option>
          ))}
        </select>
      </div>

      {/* Вибір кількості */}
      <div className="select-section">
        <label htmlFor="count">Виберіть кількість:</label>
        <select
          id="count"
          value={selectedCount}
          onChange={(e) => setSelectedCount(parseInt(e.target.value))}
        >
          {counts.map((count) => (
            <option key={count} value={count}>{count}</option>
          ))}
        </select>
      </div>

      {/* Кнопки Add to Cart і Go Back */}
      <div className="button-group">
        <button className="add-to-cart" onClick={handleAddToCart}>Add to Cart</button>
        <button className="go-back" onClick={() => navigate(-1)}>Go Back</button>
      </div>
    </div>
  );
};

export default Item;
