// carcontext.js
import React, { createContext, useState, useEffect } from 'react';
import { fetchCars, fetchFilteredCars } from './api';

export const CarContext = createContext();

export const CarProvider = ({ children }) => {
  const [carList, setCarList] = useState([]);
  const [filteredCarList, setFilteredCarList] = useState([]); // Додаємо відфільтрований список
  const [isLoading, setIsLoading] = useState(false);
  const [totalSpeed, setTotalSpeed] = useState(0);

  // Завантажуємо всі автомобілі при першому рендерингу
  useEffect(() => {
    loadCars();
  }, []);

  const loadCars = async () => {
    setIsLoading(true);
    try {
      const cars = await fetchCars(); // Отримуємо всі автомобілі з API
      setCarList(cars);
      setFilteredCarList(cars); // Ініціалізуємо відфільтрований список
    } catch (error) {
      console.error('Error fetching cars:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleSearch = async (searchTerm) => {
    setIsLoading(true);
    try {
      if (searchTerm.trim() === "") {
        setFilteredCarList(carList); // Показуємо всі автомобілі, якщо поле пошуку порожнє
      } else {
        const filteredCars = await fetchFilteredCars(searchTerm);
        setFilteredCarList(filteredCars); // Оновлюємо лише відфільтрований список
      }
    } catch (error) {
      console.error('Error searching cars:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleSortAsc = () => {
    const sortedCars = [...filteredCarList].sort((a, b) => a.power - b.power);
    setFilteredCarList(sortedCars);
  };

  const handleSortDesc = () => {
    const sortedCars = [...filteredCarList].sort((a, b) => b.power - a.power);
    setFilteredCarList(sortedCars);
  };

  const handleTotalSpeed = () => {
    const total = filteredCarList.reduce((sum, car) => sum + (car.maxSpeed || 0), 0);
    setTotalSpeed(total);
  };

  return (
    <CarContext.Provider
      value={{
        carList: filteredCarList, // Передаємо відфільтрований список
        totalSpeed,
        handleSearch,
        handleSortAsc,
        handleSortDesc,
        handleTotalSpeed,
        isLoading,
      }}
    >
      {children}
    </CarContext.Provider>
  );
};
