import React, { useContext, useState } from 'react';
import { useDispatch } from 'react-redux';
import { addToCart } from './cartSlice';
import { CarContext } from './carcontext';
import CarCard from './carcrad';
import './catalog.css';

const Catalog = () => {
  const { carList, isLoading } = useContext(CarContext);
  const [searchTerm, setSearchTerm] = useState('');
  const [filteredCars, setFilteredCars] = useState(carList);
  const dispatch = useDispatch();

  React.useEffect(() => {
    setFilteredCars(carList);
  }, [carList]);

  const handleSearchChange = (e) => {
    const value = e.target.value;
    setSearchTerm(value);

    if (value) {
      const filtered = carList.filter(car =>
        car.brand.toLowerCase().includes(value.toLowerCase())
      );
      setFilteredCars(filtered);
    } else {
      setFilteredCars(carList);
    }
  };

  if (isLoading) return <div>Loading...</div>;

  return (
    <div className="catalog-page">
      <div className="controls">
        <input
          type="text"
          placeholder="Search by brand"
          value={searchTerm}
          onChange={handleSearchChange}
        />
      </div>
      <div className="catalog-grid">
        {filteredCars.map(car => (
          <div key={car.id} className="car-card">
            <CarCard car={car} />
            <button onClick={() => dispatch(addToCart(car))}>
              Add to Cart
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Catalog;
