import React, { useContext, useState } from 'react';
import { CarContext } from './carcontext';
import CarCard from './carcrad';
import Spinner from './spinner';
import './catalog.css';
import './button.css';
import './footer.css';

const Catalog = () => {
  const { carList, totalSpeed, handleSearch, handleSortAsc, handleSortDesc, handleTotalSpeed, isLoading } = useContext(CarContext);
  const [searchTerm, setSearchTerm] = useState('');
  const [filteredCars, setFilteredCars] = useState(carList);

  React.useEffect(() => {
    setFilteredCars(carList);
  }, [carList]);

  const handleSearchChange = (e) => {
    const value = e.target.value;
    setSearchTerm(value);

    if (value) {
      const filtered = carList.filter(car =>
        car.brand.toLowerCase().includes(value.toLowerCase())
      );
      setFilteredCars(filtered);
    } else {
      setFilteredCars(carList);
    }
  };

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    if (searchTerm) {
      const filtered = carList.filter(car =>
        car.brand.toLowerCase().includes(searchTerm.toLowerCase())
      );
      setFilteredCars(filtered);
    } else {
      setFilteredCars(carList);
    }
  };

  const clearSearch = () => {
    setSearchTerm('');
    setFilteredCars(carList);
  };

  if (isLoading) {
    return <Spinner />;
  }

  return (
    <div className="catalog-page">
      <div className="controls">
        <button onClick={handleSortAsc}>Sort by Power (down)</button>
        <button onClick={handleSortDesc}>Sort by Power (up)</button>

        <form onSubmit={handleSearchSubmit} style={{ display: 'flex', gap: '10px', alignItems: 'center' }}>
          <input
            type="text"
            placeholder="Search by brand"
            value={searchTerm}
            onChange={handleSearchChange}
            list="brand-suggestions" 
          />
          <datalist id="brand-suggestions">
            {carList
              .filter(car => car.brand.toLowerCase().includes(searchTerm.toLowerCase()))
              .map((car, index) => (
                <option key={index} value={car.brand} />
              ))}
          </datalist>
          <button type="submit">Search</button>
          <button type="button" onClick={clearSearch}>Clear</button>
        </form>

        <button onClick={handleTotalSpeed}>Count Total Speed</button>
      </div>

      <div id="total-speed-container">
        <h3>Total Max Speed of Cars in Catalog: <span>{totalSpeed}</span> km/h</h3>
      </div>

      <div className="catalog-grid">
        {filteredCars.map((car, index) => (
          <CarCard key={car.id || index} car={car} />
        ))}
      </div>
    </div>
  );
};

export default Catalog;
