import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { fetchCars } from './api';
import './catalog.css';

const Catalog = () => {
  const [cars, setCars] = useState([]); // Список автомобілів
  const [searchTerm, setSearchTerm] = useState(''); // Пошуковий термін
  const navigate = useNavigate();

  useEffect(() => {
    const loadCars = async () => {
      try {
        const data = await fetchCars(searchTerm); // Запит до API
        setCars(data);
      } catch (error) {
        console.error('Помилка завантаження автомобілів:', error);
      }
    };
    loadCars();
  }, [searchTerm]); // Викликається при зміні пошукового терміну

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleCarClick = (carId) => {
    navigate(`/item/${carId}`); // Перехід на сторінку деталей автомобіля
  };

  return (
    <div className="container">
      <h2>Каталог автомобілів</h2>
      <input
        type="text"
        placeholder="Пошук..."
        onChange={handleSearchChange}
      />
      <div className="catalog-grid">
        {cars.length > 0 ? (
          cars.map((car) => (
            <div key={car.id} className="car-card" onClick={() => handleCarClick(car.id)}>
              <h3>{car.brand} {car.model}</h3>
              <p>Макс. швидкість: {car.maxSpeed} км/год</p>
              <img src={`http://localhost:5008${car.image}`} alt={car.model} />
            </div>
          ))
        ) : (
          <p>Нічого не знайдено</p>
        )}
      </div>
    </div>
  );
};

export default Catalog;
