import React, { useEffect, useState } from 'react';
import { fetchCart, clearCart } from './api';
import './cart.css';

const Cart = () => {
  const [cart, setCart] = useState([]);

  useEffect(() => {
    const loadCart = async () => {
      try {
        const data = await fetchCart();
        setCart(data);
      } catch (error) {
        console.error('Помилка завантаження кошика:', error);
      }
    };

    loadCart();
  }, []);

  const handleClearCart = async () => {
    try {
      await clearCart();
      setCart([]);
      alert('Кошик очищено!');
    } catch (error) {
      console.error('Помилка очищення кошика:', error);
    }
  };

  return (
    <div className="cart-page">
      <h2>Ваш кошик</h2>
      {cart.length > 0 ? (
        <div>
          {cart.map((item, index) => (
            <div key={index} className="cart-item">
              <h3>{item.brand} {item.model}</h3>
              <p>Комплектація: {item.configuration}</p>
              <p>Кількість: {item.count}</p>
              <p>Максимальна швидкість: {item.maxSpeed} км/год</p>
              <img src={`http://localhost:5013${item.image}`} alt={item.model} />
            </div>
          ))}
          <button className="clear-cart-button" onClick={handleClearCart}>Очистити кошик</button>
        </div>
      ) : (
        <p>Кошик порожній</p>
      )}
    </div>
  );
};

export default Cart;
