import React, { createContext, useState, useEffect } from 'react';
import { fetchCars, fetchFilteredCars } from './api';

export const CarContext = createContext();

export const CarProvider = ({ children }) => {
  const [carList, setCarList] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [totalSpeed, setTotalSpeed] = useState(0); // Add totalSpeed state

  useEffect(() => {
    const loadCars = async () => {
      setIsLoading(true);
      const cars = await fetchCars();
      setCarList(cars);
      setIsLoading(false);
    };
    loadCars();
  }, []);

  const handleSearch = async (searchTerm) => {
    setIsLoading(true);
    const filteredCars = await fetchFilteredCars(searchTerm);
    setCarList(filteredCars);
    setIsLoading(false);
  };

  const handleSortAsc = () => {
    const sortedCars = [...carList].sort((a, b) => a.power - b.power);
    setCarList(sortedCars);
  };

  const handleSortDesc = () => {
    const sortedCars = [...carList].sort((a, b) => b.power - a.power);
    setCarList(sortedCars);
  };

  const handleTotalSpeed = () => {
    const total = carList.reduce((sum, car) => sum + (car.maxSpeed || 0), 0);
    setTotalSpeed(total);
  };


  return (
    <CarContext.Provider
      value={{
        carList,
        totalSpeed,
        isLoading,
        handleSearch,
        handleSortAsc,
        handleSortDesc,
        handleTotalSpeed
      }}
    >
      {children}
    </CarContext.Provider>
  );
};
