import React, { useState, useEffect } from 'react';
import { fetchCars } from './api';  // Імпортуємо функції API
import './catalog.css';

const Catalog = () => {
  const [cars, setCars] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [totalSpeed, setTotalSpeed] = useState(0);

  useEffect(() => {
    const loadCars = async () => {
      const data = await fetchCars(searchTerm);  // Викликаємо API з пошуковим терміном
      setCars(data);
    };
    loadCars();
  }, [searchTerm]);

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);  // Змінюємо пошуковий термін
  };

  const calculateTotalSpeed = () => {
    const totalSpeed = cars.reduce((acc, car) => acc + car.maxSpeed, 0);
    setTotalSpeed(totalSpeed);
  };

  return (
    <div className="container">
      <h2>Каталог автомобілів</h2>
      <div className="controls">
        <input
          type="text"
          placeholder="Пошук..."
          value={searchTerm}
          onChange={handleSearchChange}  // Обробка зміни пошукового терміну
        />
      </div>

      <div className="controls">
        <button onClick={calculateTotalSpeed}>Підрахувати загальну швидкість</button>
      </div>

      <h3>Загальна швидкість всіх автомобілів: {totalSpeed} км/год</h3>

      <div className="catalog-grid">
        {cars.length > 0 ? (
          cars.map((car) => (
            <div key={car.id} className="car-card">
              <h3>{car.brand} {car.model}</h3>
              <p>Макс. швидкість: {car.maxSpeed} км/год</p>
              <img src={`http://localhost:5004${car.image}`} alt={car.model} />
            </div>
          ))
        ) : (
          <p>Нічого не знайдено</p>
        )}
      </div>
    </div>
  );
};

export default Catalog;
