// api.js
import axios from 'axios';

const API_URL = 'http://localhost:5003'; // Базова URL-адреса вашого бекенду

// Отримати всі автомобілі
export const fetchCars = async () => {
  const response = await axios.get(`${API_URL}/cars`);
  return response.data;
};

// Фільтрувати автомобілі за параметрами пошуку
export const fetchFilteredCars = async (searchTerm) => {
  const response = await axios.get(`${API_URL}/cars`, {
    params: { search: searchTerm },
  });
  return response.data;
};
