import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom'; // Навігація
import { fetchCars } from './api'; // Імпорт API-функцій
import { debounce } from 'lodash'; // Дебаунс
import './catalog.css'; // Стилі

const Catalog = () => {
  const [cars, setCars] = useState([]); // Автомобілі
  const [searchTerm, setSearchTerm] = useState(''); // Пошук
  const [sortOrder, setSortOrder] = useState(''); // Сортування
  const [loading, setLoading] = useState(false); // Завантаження спінера
  const navigate = useNavigate(); // Для переходу на сторінку деталей

  // Завантаження автомобілів
  useEffect(() => {
    const loadCars = async () => {
      setLoading(true);
      try {
        const data = await fetchCars(searchTerm, sortOrder);
        setCars(data);
      } catch (error) {
        console.error('Помилка при завантаженні автомобілів:', error);
      } finally {
        setLoading(false);
      }
    };

    loadCars();
  }, [searchTerm, sortOrder]);

  // Обробка пошуку
  const handleSearchChange = debounce((e) => {
    setSearchTerm(e.target.value);
  }, 300);

  // Обробка сортування
  const handleSortOrder = (order) => {
    setSortOrder(order);
  };

  // Перехід до деталей автомобіля
  const handleCarClick = (carId) => {
    navigate(`/item/${carId}`);
  };

  return (
    <div className="container">
      <h2>Каталог автомобілів</h2>

      {/* Пошук */}
      <div className="search-container">
        <input
          type="text"
          placeholder="Пошук..."
          onChange={handleSearchChange}
        />
      </div>

      {/* Сортування */}
      <div className="controls">
        <button onClick={() => handleSortOrder('asc')}>Сортувати за зростанням</button>
        <button onClick={() => handleSortOrder('desc')}>Сортувати за спаданням</button>
      </div>

      {/* Список автомобілів */}
      {loading ? (
        <div className="spinner-container">
          <div className="spinner"></div>
        </div>
      ) : (
        <div className="catalog-grid">
          {cars.length > 0 ? (
            cars.map((car) => (
              <div key={car.id} className="car-card" onClick={() => handleCarClick(car.id)}>
                <h3>{car.brand} {car.model}</h3>
                <p>Макс. швидкість: {car.maxSpeed} км/год</p>
                <img src={`http://127.0.0.1:5012${car.image}`} alt={car.model} />
              </div>
            ))
          ) : (
            <p>Нічого не знайдено</p>
          )}
        </div>
      )}
    </div>
  );
};

export default Catalog;
