import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom'; // Для переходу на сторінку з деталями
import { fetchCars } from './api'; // Функція для отримання автомобілів
import './catalog.css';

const Catalog = () => {
  const [cars, setCars] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const loadCars = async () => {
      const data = await fetchCars(); // Отримуємо всі автомобілі
      setCars(data);
    };

    loadCars();
  }, []);

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const filterCars = () => {
    return cars.filter((car) =>
      car.brand.toLowerCase().includes(searchTerm.toLowerCase()) ||
      car.model.toLowerCase().includes(searchTerm.toLowerCase())
    );
  };

  const handleCarClick = (carId) => {
    navigate(`/item/${carId}`); // Перехід на сторінку деталей
  };

  return (
    <div className="catalog">
      <input
        type="text"
        placeholder="Пошук..."
        value={searchTerm}
        onChange={handleSearchChange}
      />
      <div className="catalog-grid">
        {filterCars().map((car) => (
          <div key={car.id} className="car-card" onClick={() => handleCarClick(car.id)}>
            <h3>{car.brand} {car.model}</h3>
            <img src={`http://localhost:5004${car.image}`} alt={car.model} />
          </div>
        ))}
      </div>
    </div>
  );
};

export default Catalog;
