import axios from 'axios';

const API_URL = 'http://localhost:5008';  // Порт вашого сервера

// Функція для отримання всіх автомобілів з пошуком та сортуванням
export const fetchCars = async (searchTerm = '', sortOrder = '') => {
  try {
    const response = await axios.get(`${API_URL}/cars`, {
      params: { searchTerm, sort: sortOrder },  // Параметри для пошуку та сортування
    });
    return response.data;
  } catch (error) {
    console.error('Помилка при отриманні даних:', error);
    return [];
  }
};
