import React, { useEffect, useState } from 'react';
import { useDispatch } from 'react-redux';
import { fetchCartItems, updateCartQuantity, removeFromCart, clearCartItems } from './api';
import { Link } from 'react-router-dom';
import './Cart.css';

const Cart = () => {
  const [cartItems, setCartItems] = useState([]); // Ініціалізуємо як порожній масив
  const [totalPrice, setTotalPrice] = useState(0);

  const dispatch = useDispatch();

  const loadCartItems = async () => {
    // Завантажуємо актуальні дані кошика з сервера
    const items = await fetchCartItems();
    setCartItems(items || []);
  };

  useEffect(() => {
    loadCartItems();
  }, []);

  useEffect(() => {
    // Підрахунок загальної суми
    if (cartItems && cartItems.length > 0) {
      const total = cartItems.reduce((sum, item) => sum + item.price * item.count, 0);
      setTotalPrice(total);
    } else {
      setTotalPrice(0); // Якщо кошик порожній, сума дорівнює 0
    }
  }, [cartItems]);

  const handleQuantityChange = async (id, quantity) => {
    if (quantity < 1) return;
    await updateCartQuantity({ id, quantity });
    loadCartItems(); // Перезавантаження даних із сервера
  };

  const handleRemoveItem = async (id) => {
    await removeFromCart(id);
    loadCartItems(); // Перезавантаження даних із сервера
  };

  const handleClearCart = async () => {
    await clearCartItems();
    setCartItems([]); // Локальне очищення стану
  };

  const handlePurchase = () => {
    alert('Дякуємо за покупку! Ваше замовлення обробляється.');
    handleClearCart();
  };

  return (
    <div className="cart-page">
      <h2>Кошик</h2>
      {cartItems && cartItems.length > 0 ? (
        <>
          {cartItems.map((item) => (
            <div className="cart-item" key={item.id}>
              <img src={`http://127.0.0.1:5006${item.image}`} alt={item.model} />
              <div>
                <h3>{item.brand} {item.model}</h3>
                <p className="price">Ціна: {item.price} грн</p>
              </div>
              <div className="quantity-controls">
                <button onClick={() => handleQuantityChange(item.id, item.count - 1)}>-</button>
                <input type="number" value={item.count} readOnly />
                <button onClick={() => handleQuantityChange(item.id, item.count + 1)}>+</button>
              </div>
              <button
                className="delete-button"
                onClick={() => handleRemoveItem(item.id)}
              >
                Видалити
              </button>
            </div>
          ))}
          <div className="total-container">Загальна сума: {totalPrice} грн</div>
          <button className="purchase-button" onClick={handlePurchase}>
            Купити
          </button>
          <Link to="/catalog">
            <button className="back-to-menu-button">Назад до меню</button>
          </Link>
        </>
      ) : (
        <div className="empty-cart">Кошик порожній</div>
      )}
    </div>
  );
};

export default Cart;
