import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { fetchCarDetails } from './api';  // Імпортуємо функцію API
import './item.css';

const Item = () => {
  const { id } = useParams();
  const [car, setCar] = useState(null);
  const [selectedConfig, setSelectedConfig] = useState('Базова');
  const [selectedCount, setSelectedCount] = useState(1);
  const [totalSpeed, setTotalSpeed] = useState(0);

  useEffect(() => {
    const loadCarDetails = async () => {
      const data = await fetchCarDetails(id);
      setCar(data);
    };

    loadCarDetails();
  }, [id]);

  const handleCountTotalSpeed = () => {
    const totalSpeed = car ? car.maxSpeed * selectedCount : 0;
    setTotalSpeed(totalSpeed);
  };

  if (!car) {
    return <div>Завантаження...</div>;
  }

  return (
    <div className="container">
      <h2>{car.brand} {car.model}</h2>
      <img src={`http://localhost:5004${car.image}`} alt={car.model} />
      <p>Потужність: {car.power} hp</p>
      <p>Макс. швидкість: {car.maxSpeed} км/год</p>

      <div className="select-field">
        <label>Комплектація:</label>
        <select value={selectedConfig} onChange={(e) => setSelectedConfig(e.target.value)}>
          <option value="Базова">Базова</option>
          <option value="Комфорт">Комфорт</option>
          <option value="Спорт">Спорт</option>
          <option value="Люкс">Люкс</option>
        </select>
      </div>

      <div className="select-field">
        <label>Кількість:</label>
        <select value={selectedCount} onChange={(e) => setSelectedCount(Number(e.target.value))}>
          {Array.from({ length: 20 }, (_, i) => i + 1).map((count) => (
            <option key={count} value={count}>{count}</option>
          ))}
        </select>
      </div>

      <button className="add-to-cart">Додати до кошика</button>

      <div id="total-speed-container">
        <h3>Загальна швидкість: {totalSpeed} км/год</h3>
      </div>

      <button className="count-total-speed" onClick={handleCountTotalSpeed}>
        Підрахувати загальну швидкість
      </button>

      <button className="back-button" onClick={() => window.history.back()}>Назад до каталогу</button>
    </div>
  );
};

export default Item;
