import React, { useState, useEffect } from 'react';
import { fetchCars } from './api';  // Імпортуємо функцію fetchCars для отримання даних
import { debounce } from 'lodash';  // Імпортуємо debounce для оптимізації запитів
import './catalog.css';

const Catalog = () => {
  const [cars, setCars] = useState([]);  // Зберігаємо всі автомобілі
  const [searchTerm, setSearchTerm] = useState('');  // Пошуковий термін
  const [totalSpeed, setTotalSpeed] = useState(0);  // Загальна швидкість
  const [sortOrder, setSortOrder] = useState('');  // Порядок сортування
  const [filteredCars, setFilteredCars] = useState([]);  // Відфільтровані автомобілі

  // Завантажуємо автомобілі при зміні пошукового терміну або порядку сортування
  useEffect(() => {
    const loadCars = async () => {
      console.log("Searching for cars with term:", searchTerm);  // Виведемо пошуковий термін в консоль для перевірки
      const data = await fetchCars(searchTerm, sortOrder);  // Отримуємо дані з бекенду
      console.log("Cars data received:", data);  // Перевіряємо, чи ми отримали дані
      setCars(data);
      setFilteredCars(data);  // Встановлюємо відфільтровані автомобілі
    };

    if (searchTerm) {  // Якщо пошуковий термін не порожній
      loadCars();
    } else {
      setFilteredCars(cars);  // Якщо немає терміну, відображаємо всі автомобілі
    }
  }, [searchTerm, sortOrder]);  // Викликається при зміні пошукового терміну чи сортування

  // Функція для сортування автомобілів
  const sortCars = (order) => {
    setSortOrder(order);  // Оновлюємо порядок сортування
  };

  // Підрахунок загальної швидкості всіх автомобілів
  const calculateTotalSpeed = () => {
    const total = filteredCars.reduce((acc, car) => acc + car.maxSpeed, 0);  // Підсумовуємо максимальні швидкості
    setTotalSpeed(total);  // Оновлюємо стан загальної швидкості
  };

  // Обробник змін пошукового терміну
  const handleSearchChange = debounce((e) => {
    setSearchTerm(e.target.value);  // Оновлюємо пошуковий термін
  }, 300);  // 300 мс затримка між введенням символів і відправкою запиту

  // Обробник кліку на автомобіль
  const handleCarClick = (carId) => {
    window.location.href = `/item/${carId}`;  // Перехід на сторінку автомобіля
  };

  return (
    <div className="container">
      <h2>Каталог автомобілів</h2>

      {/* Панель пошуку */}
      <input
        type="text"
        placeholder="Пошук..."
        value={searchTerm}
        onChange={handleSearchChange}  // Оновлюємо значення пошукового терміну при введенні
      />

      {/* Кнопки сортування */}
      <div className="controls">
        <button onClick={() => sortCars('asc')}>Сортувати за зростанням</button>
        <button onClick={() => sortCars('desc')}>Сортувати за спаданням</button>
      </div>

      {/* Підрахунок загальної швидкості */}
      <div id="total-speed-container">
        <h3>Загальна швидкість всіх автомобілів: {totalSpeed} км/год</h3>
      </div>

      <button className="count-total-speed" onClick={calculateTotalSpeed}>
        Підрахувати загальну швидкість
      </button>

      {/* Виведення автомобілів */}
      <div className="catalog-grid">
        {filteredCars.length > 0 ? (
          filteredCars.map((car) => (
            <div key={car.id} className="car-card" onClick={() => handleCarClick(car.id)}>
              <h3>{car.brand} {car.model}</h3>
              <p>Макс. швидкість: {car.maxSpeed} км/год</p>
              <img src={`http://localhost:5008${car.image}`} alt={car.model} />
            </div>
          ))
        ) : (
          <p>Нічого не знайдено</p>
        )}
      </div>
    </div>
  );
};

export default Catalog;
