import React, { useState, useEffect } from 'react';
import { fetchCars, searchCars, sortCars } from './api'; 

const Catalog = () => {
  const [cars, setCars] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [sortOrder, setSortOrder] = useState('asc');
  const [totalSpeed, setTotalSpeed] = useState(0);

  useEffect(() => {
    const loadCars = async () => {
      const data = await fetchCars(); 
      setCars(data);
    };

    loadCars();
  }, []);

  useEffect(() => {
    const loadSearchCars = async () => {
      const data = await searchCars(searchTerm);
      setCars(data);
    };

    if (searchTerm) loadSearchCars();
  }, [searchTerm]);

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleSortChange = async (order) => {
    const data = await sortCars(order, searchTerm);
    setCars(data);
    setSortOrder(order);
  };

  const calculateTotalSpeed = () => {
    const totalSpeed = cars.reduce((acc, car) => acc + car.maxSpeed, 0);
    setTotalSpeed(totalSpeed);
  };

  return (
    <div className="container">
      <h2>Каталог автомобілів</h2>
      <input
        type="text"
        placeholder="Пошук..."
        value={searchTerm}
        onChange={handleSearchChange}
      />

      <div className="controls">
        <button onClick={() => handleSortChange('asc')}>Сортувати за зростанням</button>
        <button onClick={() => handleSortChange('desc')}>Сортувати за спаданням</button>
      </div>

      <div id="total-speed-container">
        <h3>Загальна швидкість всіх автомобілів: {totalSpeed} км/год</h3>
      </div>

      <button className="count-total-speed" onClick={calculateTotalSpeed}>
        Підрахувати загальну швидкість
      </button>

      <div className="catalog-grid">
        {cars.length > 0 ? (
          cars.map((car) => (
            <div key={car.id} className="car-card">
              <h3>{car.brand} {car.model}</h3>
              <p>Макс. швидкість: {car.maxSpeed} км/год</p>
              <img src={`http://localhost:5008${car.image}`} alt={car.model} />
            </div>
          ))
        ) : (
          <p>Нічого не знайдено</p>
        )}
      </div>
    </div>
  );
};

export default Catalog;
