import axios from 'axios';

const API_URL = 'http://localhost:5004/api';

export const fetchCars = async () => {
  try {
    const response = await axios.get('http://localhost:5004/api/cars');
    return response.data; // Переконайтеся, що сервер повертає масив
  } catch (error) {
    console.error('Помилка при отриманні даних:', error);
    return []; // Якщо є помилка, повертаємо порожній масив
  }
};

export const fetchCarDetails = async (id) => {
  const response = await axios.get(`${API_URL}/cars/${id}`);
  return response.data;
};

export const addToCart = async (carData) => {
  const response = await axios.post(`${API_URL}/cart`, carData);
  return response.data;
};

export const fetchCart = async () => {
  const response = await axios.get(`${API_URL}/cart`);
  return response.data;
};

export const clearCart = async () => {
  const response = await axios.delete(`${API_URL}/cart`);
  return response.data;
};
