import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom'; // Для отримання ID з URL
import { fetchCarDetails } from './api'; // Функція для отримання деталей автомобіля
import './item.css';

const Item = () => {
  const { id } = useParams(); // Отримуємо ID з URL
  const [car, setCar] = useState(null);

  useEffect(() => {
    const loadCarDetails = async () => {
      const data = await fetchCarDetails(id); // Отримуємо дані автомобіля через API
      setCar(data);
    };

    loadCarDetails();
  }, [id]);

  if (!car) {
    return <div>Завантаження...</div>;
  }

  return (
    <div className="car-details-page">
      <h2>{car.brand} {car.model}</h2>
      <img src={`http://localhost:5004${car.image}`} alt={car.model} />
      <p>Потужність: {car.power} hp</p>
      <p>Макс. швидкість: {car.maxSpeed} км/год</p>
    </div>
  );
};

export default Item;
