import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { fetchCars, addToCart } from './api';
import { debounce } from 'lodash';
import './catalog.css';

const Catalog = () => {
  const [cars, setCars] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [sortOrder, setSortOrder] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const loadCars = async () => {
      setLoading(true);
      try {
        const data = await fetchCars(searchTerm, sortOrder);
        setCars(data);
      } catch (error) {
        console.error('Помилка при завантаженні автомобілів:', error);
      } finally {
        setLoading(false);
      }
    };

    loadCars();
  }, [searchTerm, sortOrder]);

  const handleSearchChange = debounce((e) => {
    setSearchTerm(e.target.value);
  }, 300);

  const handleSortOrder = (order) => {
    setSortOrder(order);
  };

  const handleCarClick = (carId) => {
    navigate(`/item/${carId}`);
  };

  const handleAddToCart = async (carId) => {
    try {
      await addToCart({ id: carId, configuration: 'Базова', count: 1 });
      alert('Машина додана до кошика!');
    } catch (error) {
      console.error('Помилка додавання до кошика:', error);
    }
  };

  return (
    <div className="container">
      <h2>Каталог автомобілів</h2>
      <div className="search-container">
        <input type="text" placeholder="Пошук..." onChange={handleSearchChange} />
      </div>
      <div className="controls">
        <button onClick={() => handleSortOrder('asc')}>Сортувати за зростанням</button>
        <button onClick={() => handleSortOrder('desc')}>Сортувати за спаданням</button>
      </div>
      {loading ? (
        <div className="spinner-container">
          <div className="spinner"></div>
        </div>
      ) : (
        <div className="catalog-grid">
          {cars.length > 0 ? (
            cars.map((car) => (
              <div key={car.id} className="car-card">
                <h3>{car.brand} {car.model}</h3>
                <p>Макс. швидкість: {car.maxSpeed} км/год</p>
                <img src={`http://localhost:5013${car.image}`} alt={car.model} onClick={() => handleCarClick(car.id)} />
                <button onClick={() => handleAddToCart(car.id)}>Додати до кошика</button>
              </div>
            ))
          ) : (
            <p>Нічого не знайдено</p>
          )}
        </div>
      )}
    </div>
  );
};

export default Catalog;
