import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { fetchCars } from './api';
import { debounce } from 'lodash';
import './catalog.css';

const Catalog = () => {
  const [cars, setCars] = useState([]); // Всі машини
  const [searchTerm, setSearchTerm] = useState(''); // Пошуковий термін
  const [filteredCars, setFilteredCars] = useState([]); // Фільтровані машини
  const [showDropdown, setShowDropdown] = useState(false); // Показати випадаючий список
  const [sortOrder, setSortOrder] = useState(''); // Сортування
  const navigate = useNavigate();

  // Завантаження машин із сервера
  useEffect(() => {
    const loadCars = async () => {
      try {
        const data = await fetchCars(searchTerm, sortOrder);
        setCars(data);
        setFilteredCars(data);
      } catch (error) {
        console.error('Помилка при завантаженні автомобілів:', error);
      }
    };

    loadCars();
  }, [sortOrder]);

  // Обробка зміни пошуку
  const handleSearchChange = (e) => {
    const term = e.target.value;
    setSearchTerm(term);
    setShowDropdown(term.length > 0); // Показуємо список, якщо є текст

    if (term) {
      const filtered = cars.filter((car) =>
        car.brand.toLowerCase().includes(term.toLowerCase()) ||
        car.model.toLowerCase().includes(term.toLowerCase())
      );
      setFilteredCars(filtered);
    } else {
      setFilteredCars(cars);
    }
  };

  // Вибір машини з випадаючого списку
  const handleSelectCar = (carId) => {
    navigate(`/item/${carId}`);
    setShowDropdown(false); // Закриваємо список після вибору
  };

  // Обробка сортування
  const handleSortOrder = (order) => {
    setSortOrder(order);
  };

  return (
    <div className="container">
      <h2>Каталог автомобілів</h2>

      {/* Пошук */}
      <div className="search-container">
        <input
          type="text"
          placeholder="Пошук..."
          value={searchTerm}
          onChange={handleSearchChange}
        />
        {showDropdown && (
          <ul className="dropdown">
            {filteredCars.length > 0 ? (
              filteredCars.map((car) => (
                <li key={car.id} onClick={() => handleSelectCar(car.id)}>
                  {car.brand} {car.model}
                </li>
              ))
            ) : (
              <li>Нічого не знайдено</li>
            )}
          </ul>
        )}
      </div>

      {/* Сортування */}
      <div className="controls">
        <button onClick={() => handleSortOrder('asc')}>Сортувати за зростанням</button>
        <button onClick={() => handleSortOrder('desc')}>Сортувати за спаданням</button>
      </div>

      {/* Список автомобілів */}
      <div className="catalog-grid">
        {filteredCars.map((car) => (
          <div key={car.id} className="car-card">
            <h3>{car.brand} {car.model}</h3>
            <p>Макс. швидкість: {car.maxSpeed} км/год</p>
            <img
              src={`http://127.0.0.1:5006${car.image}`}
              alt={car.model}
              onClick={() => navigate(`/item/${car.id}`)}
            />
            <button onClick={() => console.log('Додано до кошика')}>Додати до кошика</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Catalog;
