import React, { useEffect, useState } from 'react';
import { fetchCartItems, removeFromCart, updateCartQuantity } from './api';
import { useNavigate } from 'react-router-dom';
import './Cart.css';

const Cart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);
  const navigate = useNavigate();

  useEffect(() => {
    const loadCartItems = async () => {
      const items = await fetchCartItems();
      setCartItems(items);
    };
    loadCartItems();
  }, []);

  useEffect(() => {
    const calculateTotalPrice = () => {
      const total = cartItems.reduce((sum, item) => sum + item.price * item.count, 0);
      setTotalPrice(total);
    };
    calculateTotalPrice();
  }, [cartItems]);

  const handleRemove = async (id) => {
    const updatedCart = await removeFromCart(id);
    setCartItems(updatedCart);
  };

  const handleQuantityChange = async (id, count) => {
    if (count > 0) {
      const updatedCart = await updateCartQuantity({ id, count });
      setCartItems(updatedCart);
    }
  };

  const handlePurchase = () => {
    alert('Дякуємо за покупку!');
    setCartItems([]); // Очищення локального стану кошика
  };

  return (
    <div className="cart-page">
      <h2>Кошик</h2>
      {cartItems.length > 0 ? (
        <>
          {cartItems.map((item) => (
            <div className="cart-item" key={item.id}>
              <img src={`http://127.0.0.1:5005${item.image}`} alt={item.model} />
              <div>
                <h3>{item.brand} {item.model}</h3>
                <p>Ціна: {item.price} грн</p>
                <div className="cart-controls">
                  <button onClick={() => handleQuantityChange(item.id, item.count - 1)}>-</button>
                  <span>{item.count}</span>
                  <button onClick={() => handleQuantityChange(item.id, item.count + 1)}>+</button>
                </div>
              </div>
              <button className="delete-button" onClick={() => handleRemove(item.id)}>Видалити</button>
            </div>
          ))}
          <div className="total-container">
            Загальна сума: {totalPrice} грн
          </div>
          <button className="purchase-button" onClick={handlePurchase}>Купити</button>
        </>
      ) : (
        <p className="empty-cart">Ваш кошик порожній</p>
      )}
      <button className="purchase-button" onClick={() => navigate('/catalog')}>Назад до меню</button>
    </div>
  );
};

export default Cart;
