import axios from 'axios';

const API_URL = 'http://127.0.0.1:5004'; // Має збігатися з портом сервера

export const fetchCars = async (searchTerm = '', sortOrder = '') => {
  try {
    const response = await axios.get(`${API_URL}/cars`, {
      params: { search: searchTerm, sort: sortOrder }
    });
    return response.data;
  } catch (error) {
    console.error('Помилка при отриманні даних:', error);
    return [];
  }
};

export const fetchCarDetails = async (id) => {
  try {
    const response = await axios.get(`${API_URL}/cars/${id}`);
    return response.data;
  } catch (error) {
    console.error('Помилка при отриманні даних автомобіля:', error);
    return null;
  }
};
export const fetchTotalSpeed = async (searchTerm = '') => {
  try {
    const response = await axios.get(`${API_URL}/cars/total-speed`, {
      params: { search: searchTerm }, // Передаємо пошуковий термін, якщо є
    });
    return response.data.totalSpeed; // Повертаємо загальну швидкість
  } catch (error) {
    console.error('Помилка при отриманні загальної швидкості:', error);
    return 0;
  }
};


// Функція для додавання автомобіля до кошика
export const addToCart = async ({ id, configuration, count }) => {
  try {
    const response = await axios.post(`${API_URL}/cart`, { carId: id, configuration, quantity: count });
    return response.data;
  } catch (error) {
    console.error('Помилка при додаванні до кошика:', error);
    return null;
  }
};
