import axios from 'axios';

const API_URL = 'http://localhost:5008'; // Ваш сервер

// Функція для виконання базових запитів
const baseRequest = async ({ urlPath = '', method = 'GET', data = null }) => {
  try {
    const response = await axios({
      url: `${API_URL}${urlPath}`,
      method,
      headers: {
        'Content-Type': 'application/json',
      },
      data,
    });
    return response.data;
  } catch (error) {
    console.error('HTTP ERROR: ', error);
    throw error;
  }
};

// Функція для отримання всіх автомобілів
export const fetchCars = async () => {
  return await baseRequest({ method: 'GET', urlPath: '/cars' });
};

// Функція для пошуку автомобілів за терміном
export const searchCars = async (searchTerm) => {
  const queryParams = `?search=${encodeURIComponent(searchTerm)}`;
  return await baseRequest({ method: 'GET', urlPath: `/cars${queryParams}` });
};

// Функція для сортування автомобілів
export const sortCars = async (sort, searchTerm) => {
  const queryParams = `?order_by=${encodeURIComponent(sort)}&search=${encodeURIComponent(searchTerm)}`;
  return await baseRequest({ method: 'GET', urlPath: `/cars${queryParams}` });
};

// Функція для отримання автомобіля по id
export const fetchCarDetails = async (id) => {
  return await baseRequest({ method: 'GET', urlPath: `/cars/${id}` });
};
