import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom'; // Використовуємо React Router для переходів
import { fetchCars } from './api'; // Функція для отримання даних
import { debounce } from 'lodash'; // Оптимізація введення
import Spinner from './spinner'; // Імпортуємо компонент спінера
import './catalog.css'; // Стилі

const Catalog = () => {
  const [cars, setCars] = useState([]); // Дані автомобілів
  const [searchTerm, setSearchTerm] = useState(''); // Пошуковий термін
  const [totalSpeed, setTotalSpeed] = useState(0); // Загальна швидкість
  const [sortOrder, setSortOrder] = useState(''); // Сортування
  const [loading, setLoading] = useState(false); // Стан завантаження
  const navigate = useNavigate(); // Для переходів

  useEffect(() => {
    const loadCars = async () => {
      setLoading(true); // Початок завантаження
      try {
        const data = await fetchCars(searchTerm, sortOrder); // Запит до API
        setCars(data); // Оновлення стану
      } catch (error) {
        console.error('Помилка завантаження даних:', error);
      } finally {
        setLoading(false); // Завершення завантаження
      }
    };

    loadCars();
  }, [searchTerm, sortOrder]); // Залежність від пошуку та сортування

  // Обробка введення пошуку з debounce
  const handleSearchChange = debounce((e) => {
    setSearchTerm(e.target.value);
  }, 300);

  // Обробка сортування
  const handleSortOrder = (order) => {
    setSortOrder(order);
  };

  // Підрахунок загальної швидкості
  const calculateTotalSpeed = () => {
    const total = cars.reduce((sum, car) => sum + car.maxSpeed, 0);
    setTotalSpeed(total);
  };

  // Перехід на сторінку деталей автомобіля
  const handleCarClick = (carId) => {
    navigate(`/item/${carId}`);
  };

  return (
    <div className="container">
      <h2>Каталог автомобілів</h2>

      {/* Пошук */}
      <div className="search-container">
        <input
          type="text"
          placeholder="Пошук..."
          onChange={handleSearchChange}
        />
      </div>

      {/* Сортування */}
      <div className="controls">
        <button onClick={() => handleSortOrder('asc')}>Сортувати за зростанням</button>
        <button onClick={() => handleSortOrder('desc')}>Сортувати за спаданням</button>
      </div>

      {/* Загальна швидкість */}
      <div id="total-speed-container">
        <h3>Загальна швидкість всіх автомобілів: {totalSpeed} км/год</h3>
      </div>
      <button className="count-total-speed" onClick={calculateTotalSpeed}>
        Підрахувати загальну швидкість
      </button>

      {/* Спінер під час завантаження */}
      {loading ? (
        <Spinner /> // Відображення компонента спінера
      ) : (
        <div className="catalog-grid">
          {cars.length > 0 ? (
            cars.map((car) => (
              <div key={car.id} className="car-card" onClick={() => handleCarClick(car.id)}>
                <h3>{car.brand} {car.model}</h3>
                <p>Макс. швидкість: {car.maxSpeed} км/год</p>
                <img src={`http://localhost:5009${car.image}`} alt={car.model} />
              </div>
            ))
          ) : (
            <p>Нічого не знайдено</p>
          )}
        </div>
      )}
    </div>
  );
};

export default Catalog;
