import axios from 'axios';

const API_URL = 'http://localhost:5008';  // Порт вашого сервера

// Функція для отримання автомобілів за пошуковим терміном і порядком сортування
export const fetchCars = async (searchTerm, sortOrder) => {
  try {
    const response = await axios.get(`${API_URL}/cars`, {
      params: { search: searchTerm, sort: sortOrder }  // Параметри пошуку та сортування
    });
    return response.data;  // Повертаємо отримані дані
  } catch (error) {
    console.error('Помилка при отриманні даних:', error);
    return [];
  }
};
