import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { fetchCars } from './api';
import { debounce } from 'lodash';
import './catalog.css';

const Catalog = () => {
  const [cars, setCars] = useState([]); // Всі машини
  const [searchTerm, setSearchTerm] = useState(''); // Пошуковий термін
  const [sortOrder, setSortOrder] = useState(''); // Сортування
  const [filterAvailable, setFilterAvailable] = useState(false); // Фільтр наявності
  const [loading, setLoading] = useState(false); // Завантаження
  const navigate = useNavigate();

  useEffect(() => {
    const loadCars = async () => {
      setLoading(true);
      try {
        const data = await fetchCars(searchTerm, sortOrder);
        if (filterAvailable) {
          setCars(data.filter((car) => car.isAvailable)); // Показуємо лише наявні
        } else {
          setCars(data); // Показуємо всі машини
        }
      } catch (error) {
        console.error('Помилка при завантаженні автомобілів:', error);
      } finally {
        setLoading(false);
      }
    };

    loadCars();
  }, [searchTerm, sortOrder, filterAvailable]);

  const handleSearchChange = debounce((e) => {
    setSearchTerm(e.target.value);
  }, 300);

  const handleSortOrder = (order) => {
    setSortOrder(order);
  };

  const handleCarClick = (carId) => {
    navigate(`/item/${carId}`);
  };

  return (
    <div className="container">
      <h2>Каталог автомобілів</h2>

      {/* Пошук */}
      <div className="search-container">
        <input
          type="text"
          placeholder="Пошук..."
          onChange={handleSearchChange}
        />
      </div>

      {/* Фільтр і сортування */}
      <div className="controls">
        <button onClick={() => handleSortOrder('asc')}>Сортувати за зростанням</button>
        <button onClick={() => handleSortOrder('desc')}>Сортувати за спаданням</button>
        <label className="filter-available">
          <input
            type="checkbox"
            checked={filterAvailable}
            onChange={(e) => setFilterAvailable(e.target.checked)}
          />
          Показати лише наявні
        </label>
      </div>

      {/* Список автомобілів */}
      {loading ? (
        <div className="spinner-container">
          <div className="spinner"></div>
        </div>
      ) : (
        <div className="catalog-grid">
          {cars.length > 0 ? (
            cars.map((car) => (
              <div key={car.id} className="car-card">
                <h3>{car.brand} {car.model}</h3>
                <p>Макс. швидкість: {car.maxSpeed} км/год</p>
                <p>{car.isAvailable ? 'Наявний' : 'Недоступний'}</p>
                <img src={`http://127.0.0.1:5005${car.image}`} alt={car.model} onClick={() => handleCarClick(car.id)} />
                <button onClick={() => console.log('Додано до кошика')}>Додати до кошика</button>
              </div>
            ))
          ) : (
            <p>Нічого не знайдено</p>
          )}
        </div>
      )}
    </div>
  );
};

export default Catalog;
