import axios from 'axios';

const API_URL = 'http://localhost:5004';

export const fetchCarsInRange = async (range) => {
  try {
    const response = await axios.get(`${API_URL}/api/cars/${range}`);
    return response.data;
  } catch (error) {
    console.error('Помилка при отриманні даних автомобілів:', error);
    return [];  // Повертаємо порожній масив у випадку помилки
  }
};

export const fetchCarDetails = async (id) => {
  try {
    const response = await axios.get(`/api/cars/${id}`);
    return response.data;
  } catch (error) {
    console.error('Помилка при отриманні даних автомобіля:', error);
    return null;  // Якщо не знайдено машину
  }
};

export const addToCart = async (carData) => {
  try {
    const response = await axios.post(`${API_URL}/cart`, carData);
    return response.data;
  } catch (error) {
    console.error('Помилка при додаванні до кошика:', error);
    return null;
  }
};

export const fetchCart = async () => {
  try {
    const response = await axios.get(`${API_URL}/cart`);
    return response.data;
  } catch (error) {
    console.error('Помилка при отриманні кошика:', error);
    return null;
  }
};

export const clearCart = async () => {
  try {
    const response = await axios.delete(`${API_URL}/cart`);
    return response.data;
  } catch (error) {
    console.error('Помилка при очищенні кошика:', error);
    return null;
  }
};
