import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { fetchCarDetails, addToCart } from './api';
import './item.css';

const Item = () => {
  const { id } = useParams();  // Отримуємо ID з URL
  const navigate = useNavigate();  // Для навігації назад
  const [car, setCar] = useState(null);  // Стан для збереження даних про автомобіль
  const [selectedConfig, setSelectedConfig] = useState('Базова');  // Стан для вибору комплектації
  const [selectedCount, setSelectedCount] = useState(1);  // Стан для вибору кількості

  // Завантажуємо деталі автомобіля
  useEffect(() => {
    const loadCarDetails = async () => {
      try {
        // Викликаємо функцію для отримання даних про машину з серверу
        const data = await fetchCarDetails(id);
        setCar(data);
      } catch (error) {
        console.error('Помилка отримання даних автомобіля:', error);
      }
    };
    loadCarDetails();
  }, [id]);

  // Функція для додавання в кошик
  const handleAddToCart = async () => {
    try {
      await addToCart({
        id: car.id,
        configuration: selectedConfig,
        count: selectedCount,
      });
      alert('Машина додана до кошика!');
    } catch (error) {
      console.error('Помилка додавання до кошика:', error);
    }
  };

  // Якщо дані про машину не знайдено
  if (!car) {
    return <div>Машина не знайдена</div>;
  }

  console.log('Image URL:', `http://localhost:5004${car.image}`);  // Логуємо шлях до зображення

  return (
    <div className="car-details-page">
      {/* Відображення зображення автомобіля */}
      <img src={`http://localhost:5004${car.image}`} alt={car.model} />

      {/* Відображення бренду та моделі автомобіля */}
      <h2>{car.brand} {car.model}</h2>

      {/* Відображення потужності і максимальна швидкість */}
      <p>Потужність: {car.power} hp</p>
      <p>Макс. швидкість: {car.maxSpeed} км/год</p>

      {/* Вибір комплектації */}
      <div className="select-section">
        <label>Комплектація:</label>
        <select value={selectedConfig} onChange={(e) => setSelectedConfig(e.target.value)}>
          {car.configurations.map((config, index) => (
            <option key={index} value={config}>{config}</option>
          ))}
        </select>
      </div>

      {/* Вибір кількості */}
      <div className="select-section">
        <label>Кількість:</label>
        <select value={selectedCount} onChange={(e) => setSelectedCount(parseInt(e.target.value))}>
          {Array.from({ length: 20 }, (_, i) => i + 1).map((count) => (
            <option key={count} value={count}>{count}</option>
          ))}
        </select>
      </div>

      {/* Кнопки для додавання в кошик та повернення назад */}
      <div className="button-group">
        <button onClick={handleAddToCart}>Add to Cart</button>
        <button onClick={() => navigate(-1)}>Go Back</button>
      </div>
    </div>
  );
};

export default Item;
