// api.js
import axios from 'axios';

const API_URL = 'http://localhost:3002';  // Потрібно вказати тільки базову URL-адресу без /cars

// Запит для отримання всіх авто
export const fetchCars = async () => {
    const response = await axios.get(`${API_URL}/cars`);  // Тепер додається лише /cars
    return response.data;
};

// Запит для пошуку авто за фільтрами
export const fetchFilteredCars = async (searchTerm) => {
    const response = await axios.get(`${API_URL}/cars`, {
        params: { search: searchTerm }
    });
    return response.data;
};
