import axios from 'axios';

const API_URL = 'http://localhost:5008'; // Порт вашого сервера

// Функція для отримання всіх автомобілів з параметрами пошуку та сортування
export const fetchCars = async (searchTerm = '', sortOrder = '') => {
  try {
    const response = await axios.get(`${API_URL}/cars`, {
      params: { search: searchTerm, sort: sortOrder }  // Передаємо пошуковий термін та порядок сортування
    });
    return response.data;
  } catch (error) {
    console.error('Помилка при отриманні даних:', error);
    return [];
  }
};

// Функція для отримання деталей конкретного автомобіля
export const fetchCarDetails = async (id) => {
  try {
    const response = await axios.get(`${API_URL}/cars/${id}`);
    return response.data;
  } catch (error) {
    console.error('Помилка при отриманні даних автомобіля:', error);
    return null;
  }
};

// Функція для додавання автомобіля до кошика
export const addToCart = async ({ id, configuration, count }) => {
  try {
    const response = await axios.post(`${API_URL}/cart`, { carId: id, configuration, quantity: count });
    return response.data;
  } catch (error) {
    console.error('Помилка при додаванні до кошика:', error);
    return null;
  }
};

// Функція для отримання кошика
export const fetchCart = async () => {
  try {
    const response = await axios.get(`${API_URL}/cart`);
    return response.data;
  } catch (error) {
    console.error('Помилка при отриманні кошика:', error);
    return [];
  }
};

// Функція для очищення кошика
export const clearCart = async () => {
  try {
    const response = await axios.delete(`${API_URL}/cart`);
    return response.data;
  } catch (error) {
    console.error('Помилка при очищенні кошика:', error);
    return null;
  }
};
