import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Header from './header'; // Компонент шапки
import Footer from './footer'; // Компонент підвалу
import Main from './main'; // Головна сторінка
import Catalog from './catalog'; // Каталог автомобілів
import Item from './item'; // Деталі автомобіля
import Cart from './cart'; // Кошик
import './app.css'; // Глобальні стилі

const App = () => {
  return (
    <Router>
      <div id="app">
        <Header />
        <main>
          <Routes>
            <Route path="/" element={<Main />} /> {/* Головна сторінка */}
            <Route path="/catalog" element={<Catalog />} /> {/* Каталог автомобілів */}
            <Route path="/item/:id" element={<Item />} /> {/* Деталі автомобіля */}
            <Route path="/cart" element={<Cart />} /> {/* Сторінка кошика */}
          </Routes>
        </main>
        <Footer />
      </div>
    </Router>
  );
};

export default App;
