import axios from 'axios';

const API_URL = 'http://127.0.0.1:5012'; 


export const fetchCars = async (searchTerm = '', sortOrder = '') => {
  try {
    const response = await axios.get(`${API_URL}/cars`, {
      params: { search: searchTerm, sort: sortOrder },
    });
    return response.data;
  } catch (error) {
    console.error('Помилка при отриманні автомобілів:', error);
    return [];
  }
};

// Отримання деталей автомобіля
export const fetchCarDetails = async (id) => {
  try {
    const response = await axios.get(`${API_URL}/cars/${id}`);
    return response.data;
  } catch (error) {
    console.error('Помилка при отриманні деталей автомобіля:', error);
    return null;
  }
};

// Отримання загальної швидкості
export const fetchTotalSpeed = async (searchTerm = '') => {
  try {
    const response = await axios.get(`${API_URL}/cars/total-speed`, {
      params: { search: searchTerm.trim() }, // Передаємо очищений пошуковий запит
    });
    return response.data.totalSpeed;
  } catch (error) {
    console.error('Помилка при підрахунку загальної швидкості:', error);
    throw error; // Передаємо помилку для подальшої обробки
  }
};


// Додавання автомобіля до кошика
export const addToCart = async ({ id, configuration, count }) => {
  try {
    const response = await axios.post(`${API_URL}/cart`, {
      carId: id,
      configuration,
      quantity: count,
    });
    return response.data;
  } catch (error) {
    console.error('Помилка при додаванні до кошика:', error);
    return null;
  }
};
