import axios from 'axios';

const API_URL = 'http://127.0.0.1:5005';

// Отримання товарів у кошику
export const fetchCartItems = async () => {
  try {
    const response = await axios.get(`${API_URL}/cart`);
    return Array.isArray(response.data) ? response.data : [];
  } catch (error) {
    console.error('Помилка завантаження кошика:', error);
    return [];
  }
};

// Оновлення кількості товару
export const updateCartQuantity = async ({ id, quantity }) => {
  try {
    const response = await axios.put(`${API_URL}/cart/${id}`, { quantity });
    return Array.isArray(response.data) ? response.data : [];
  } catch (error) {
    console.error('Помилка оновлення кількості:', error);
    return [];
  }
};

// Видалення товару з кошика
export const removeFromCart = async (id) => {
  try {
    const response = await axios.delete(`${API_URL}/cart/${id}`);
    return Array.isArray(response.data) ? response.data : [];
  } catch (error) {
    console.error('Помилка видалення товару з кошика:', error);
    return [];
  }
};
