import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { removeFromCart, updateQuantity } from './actions';
import './Сart.css';

const Cart = () => {
  const cart = useSelector((state) => state.cart);
  const dispatch = useDispatch();

  const handleRemove = (id) => {
    dispatch(removeFromCart(id));
  };

  const handleQuantityChange = (id, quantity) => {
    if (quantity > 0) {
      dispatch(updateQuantity(id, quantity));
    }
  };

  const totalPrice = cart.reduce((total, item) => total + item.price * item.quantity, 0);

  return (
    <div className="cart-page">
      <h2>Кошик</h2>
      {cart.length === 0 ? (
        <p className="empty-cart">Ваш кошик порожній.</p>
      ) : (
        <>
          <div className="cart-items">
            {cart.map((item) => (
              <div key={item.id} className="cart-item">
                <img src={`http://127.0.0.1:5005${item.image}`} alt={item.name} />
                <div>
                  <h3>{item.name}</h3>
                  <p>Ціна: {item.price} грн</p>
                  <div className="quantity-controls">
                    <button onClick={() => handleQuantityChange(item.id, item.quantity - 1)}>-</button>
                    <span>{item.quantity}</span>
                    <button onClick={() => handleQuantityChange(item.id, item.quantity + 1)}>+</button>
                  </div>
                </div>
                <button className="remove-button" onClick={() => handleRemove(item.id)}>Видалити</button>
              </div>
            ))}
          </div>
          <div className="cart-summary">
            <h3>Загальна сума: {totalPrice} грн</h3>
          </div>
        </>
      )}
    </div>
  );
};

export default Cart;
