import React, { useEffect, useState } from 'react';
import { fetchCartItems, removeFromCart, updateCartQuantity } from './api';
import './Cart.css';

const Cart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);

  // Завантаження товарів з кошика
  useEffect(() => {
    const loadCartItems = async () => {
      try {
        const items = await fetchCartItems();
        setCartItems(Array.isArray(items) ? items : []);
      } catch (error) {
        console.error('Помилка завантаження кошика:', error);
        setCartItems([]);
      }
    };

    loadCartItems();
  }, []);

  // Оновлення загальної суми
  useEffect(() => {
    const calculateTotalPrice = () => {
      const total = cartItems.reduce((sum, item) => sum + (item.price || 0) * (item.count || 0), 0);
      setTotalPrice(total);
    };

    calculateTotalPrice();
  }, [cartItems]);

  const handleRemove = async (id) => {
    try {
      const updatedCart = await removeFromCart(id);
      setCartItems(Array.isArray(updatedCart) ? updatedCart : []);
    } catch (error) {
      console.error('Помилка видалення товару з кошика:', error);
    }
  };

  const handleQuantityChange = async (id, quantity) => {
    if (quantity > 0) {
      try {
        const updatedCart = await updateCartQuantity({ id, quantity });
        setCartItems(Array.isArray(updatedCart) ? updatedCart : []);
      } catch (error) {
        console.error('Помилка оновлення кількості:', error);
      }
    }
  };

  const handlePurchase = () => {
    alert(`Дякуємо за покупку! Загальна сума: ${totalPrice} грн.`);
    setCartItems([]); // Очистити кошик після покупки
    setTotalPrice(0);
  };

  return (
    <div className="cart-page">
      <h2>Кошик</h2>
      {cartItems.length === 0 ? (
        <p className="empty-cart">Ваш кошик порожній.</p>
      ) : (
        <>
          <div className="cart-items">
            {cartItems.map((item) => (
              <div key={item.id} className="cart-item">
                <img src={`http://127.0.0.1:5005${item.image}`} alt={item.model} />
                <div className="cart-item-details">
                  <h3>{item.brand} {item.model}</h3>
                  <p>Комплектація: {item.configuration}</p>
                  <p>Ціна: {item.price} грн</p>
                  <div className="quantity-controls">
                    <button onClick={() => handleQuantityChange(item.id, item.count - 1)}>-</button>
                    <span>{item.count}</span>
                    <button onClick={() => handleQuantityChange(item.id, item.count + 1)}>+</button>
                  </div>
                </div>
                <button className="remove-button" onClick={() => handleRemove(item.id)}>Видалити</button>
              </div>
            ))}
          </div>
          <div className="cart-summary">
            <h3>Загальна сума: {totalPrice} грн</h3>
            <button className="purchase-button" onClick={handlePurchase}>Купити</button>
          </div>
        </>
      )}
    </div>
  );
};

export default Cart;
