import React, { useContext } from 'react';
import { useParams } from 'react-router-dom';
import { CarContext } from './carcontext';
import './item.css';

const Item = () => {
  const { id } = useParams(); // Отримуємо ID з URL
  const { carList } = useContext(CarContext); // Отримуємо список машин з контексту
  const car = carList.find((car) => car.id === parseInt(id)); // Знаходимо потрібну машину

  if (!car) {
    return <div>Машина не знайдена</div>;
  }

  return (
    <div className="car-details-page">
      <img src={car.image} alt={car.model} />
      <h2>{car.brand} {car.model}</h2>
      <p>Потужність: {car.power} hp</p>
      <p>Макс. швидкість: {car.maxSpeed} км/год</p>
    </div>
  );
};

export default Item;
