import React, { useEffect, useState } from 'react';
import { fetchCart, clearCart, updateCartItem } from './api';
import './cart.css';

const Cart = () => {
  const [cart, setCart] = useState([]);

  useEffect(() => {
    const loadCart = async () => {
      try {
        const data = await fetchCart();
        setCart(data);
      } catch (error) {
        console.error('Помилка завантаження кошика:', error);
      }
    };

    loadCart();
  }, []);

  const handleClearCart = async () => {
    try {
      await clearCart();
      setCart([]);
      alert('Кошик очищено!');
    } catch (error) {
      console.error('Помилка очищення кошика:', error);
    }
  };

  const handleUpdateCartItem = async (id, newQuantity, newConfig) => {
    try {
      await updateCartItem(id, newQuantity, newConfig);
      setCart((prevCart) =>
        prevCart.map((item) =>
          item.carId === id
            ? { ...item, count: newQuantity, configuration: newConfig }
            : item
        )
      );
      alert('Товар успішно оновлено!');
    } catch (error) {
      console.error('Помилка оновлення товару в кошику:', error);
    }
  };

  return (
    <div className="cart-page">
      <h2>Ваш кошик</h2>
      {cart.length > 0 ? (
        <div>
          {cart.map((item) => (
            <div key={item.carId} className="cart-item">
              <h3>{item.brand} {item.model}</h3>
              <p>Комплектація:</p>
              <select
                value={item.configuration}
                onChange={(e) => handleUpdateCartItem(item.carId, item.count, e.target.value)}
              >
                <option value="Базова">Базова</option>
                <option value="Комфорт">Комфорт</option>
                <option value="Спорт">Спорт</option>
                <option value="Люкс">Люкс</option>
              </select>
              <p>Кількість:</p>
              <input
                type="number"
                min="1"
                max="20"
                value={item.count}
                onChange={(e) => handleUpdateCartItem(item.carId, parseInt(e.target.value, 10), item.configuration)}
              />
              <p>Максимальна швидкість: {item.maxSpeed} км/год</p>
              <img src={`http://localhost:5005${item.image}`} alt={item.model} />
            </div>
          ))}
          <button className="clear-cart-button" onClick={handleClearCart}>Очистити кошик</button>
        </div>
      ) : (
        <p>Кошик порожній</p>
      )}
    </div>
  );
};

export default Cart;
